sys_script.script:
(function executeRule(current, previous /*null when async*/) {
		var eng = new x_wols2_cxen.CXEN_Engine();
		current.u_state = 'Synchronizing';
		current.update();
	//What kind of Task is this?
	if (current.u_calling_abstraction)
		{
		//Async abstraction call
		//Execute
			var abstractionData = JSON.parse(current.u_calling_abstraction);	
			eng.ExecuteMethodAsync(abstractionData.libraryName, abstractionData.methodPath, JSON.parse(current.u_parameter_data), abstractionData.username, abstractionData.password, current.sys_id.toString());			
		//Clear parameter data, no longer needed
		current.u_parameter_data = "[consumed]";
		current.update();			
	}
	else
		{
		//Async Function call
		//Get function code
		var func = new GlideRecord('x_wols2_cxen_cxen_functions');
		func.addQuery('sys_id', current.u_calling_function);
		func.query();
		func.next();
		//Execute
		var rtn = eng.ExecuteCodeAsync(
		func.u_code.toString(),
		func.u_namespace.toString(),
		func.u_namespace_references.toString(),
		!!func.u_return_value,
		JSON.parse(current.u_parameter_data),
		func.u_framework_references.toString(),
		func.u_cxen_reference_names.toString(),
		func.u_external_references.toString(),
		!!current.u_compile_only,
		current.sys_id.toString());
		//Clear parameter data, no longer needed
		current.u_parameter_data = "[consumed]";
		current.update();
	}
})(current, previous);
